                if (!resultDt.Columns.Contains("D" + dlbm))
                {
                    continue;
                }
                try
                {
                    if (selRows.Length > 0)
                    {
                        //已经存在
                        DataRow selRow = selRows[0];

                        if (selRow["D" + dlbm].ToString().Trim() != "")
                        {
                            double oldmj = 0;
                            double.TryParse(selRow["D" + dlbm].ToString(), out oldmj);
                            selRow["D" + dlbm] = mj + oldmj;
                        }
                        else
                        {
                            selRow["D" + dlbm] = mj;
                        }
                    }
                    else
