        private static List<double> JSXJ(List<string> dm, List<double> valueNC,int dmLength)
        {
            int index = 0;
            double tmp = 0;
            double hjNC = 0;
            for (int k = 1; k < dm.Count; k++)
            {
                //if (k == 41)
                //    k = k;
                if (dm[k].Length == dmLength)
                {
                    valueNC[index] = tmp;
                    hjNC += tmp;
                    index = k;
                    if (dm[k] == "09")
                        tmp = valueNC[k];
                    else
                        tmp = 0;
                    continue;
                }
                tmp += valueNC[k];
                if (k == dm.Count - 1)
                {
                    valueNC[index] = tmp;
                    hjNC += tmp;
                    valueNC[k + 1] = hjNC;
                }
            }
            return valueNC;
        }

